scripts.script:
  
#
# NOTE: This file will not be used if you use racoon-tool(8) to manage your
# IPsec connections. racoon-tool will process racoon-tool.conf(5) and
# generate a configuration (/var/lib/racoon/racoon.conf) and use it, instead
# of this file.
#
# Simple racoon.conf
# 
#
# Please look in /usr/share/doc/racoon/examples for
# examples that come with the source.
#
# Please read racoon.conf(5) for details, and alsoread setkey(8).
#
#
# Also read the Linux IPSEC Howto up at 
# http://www.ipsec-howto.org/t1.html 
#
log notify;
path pre_shared_key "/exc/racoon/psk.txt";
#path certificate "/exc/racoon/certs";

remote anonymous {
exchange_mode main,aggressive;
proposal {
	encryption_algorithm 3des;
	hash_algorithm sha1;
	authentication_method pre_shared_key;
	dh_group modp1024;
}
generate_policy off;
}

sainfo anonymous {
pfs_group modp768;
lifetime time 1 hour;
encryption_algorithm 3des;
authentication_algorithm hmac_md5;
compression_algorithm deflate;
}

#sainfo address 192.168.203.10[any] any address 192.168.22.0/24[any] any {
#        pfs_group modp768;
#        encryption_algorithm 3des;
#        authentication_algorithm hmac_md5;
#        compression_algorithm deflate;
#}

   
#!/usr/sbin/setkey -f

# NOTE: Do not use this file if you use racoon with racoon-tool
# utility. racoon-tool will setup SAs and SPDs automatically using
# /exc/racoon/racoon-tool.conf configuration.
# 

## Flush the SAD and SPD
#
 flush;
 spdflush;

## Some sample SPDs for use racoon
#
 spdadd 192.168.0.154 192.168.0.63 any -P out ipsec
    esp/transport//require;
                      
 spdadd 192.168.0.63 192.168.0.154 any -P in ipsec
    esp/transport//require;
